mapper.update:
UPDATE record_board
SET title = #{title}, content = #{content}, is_visible = #{isVisible}, thumbnail = #{thumbnail}
WHERE id = #{id};

UPDATE record_comment
SET content = #{cmtContent}
WHERE id = #{cmtId};

mapper.select:
SELECT title, thumbnail, user_id, is_visible, location_id
FROM record_board
WHERE user_id = 1 AND location_id = #{locationId}
ORDER BY id DESC;

SELECT rb.title, rb.thumbnail, rb.user_id, rb.is_visible, rb.location_id
FROM record_board rb
JOIN location l ON rb.location_id = l.id
WHERE rb.user_id = 1 AND l.city = #{locationCity}
ORDER BY rb.id DESC;
